import React from "react";
type TemplateProps = {
  title?: string;
  duration: string;
  background?: string;
  date?: string;
  hours?: string;
  text?: string;
  tasks?: string[];
  days?: string[];
  dayDurations?: string[];
  times?: string[];
};

export const TitleNotesBlack: React.FC<TemplateProps> = ({ title, duration, date, hours, text }) => {
  return (
    <div className="relative w-full h-full overflow-hidden bg-black rounded-xl">
      <div className="flex flex-col justify-between h-full p-6 z-10 text-white font-gloria">
        <div className="flex flex-col gap-3">
          <div className="flex justify-between items-start">
            {date && (
              <p className="text-lg font-medium">{date}</p>
            )}
            {hours && (
              <p className="text-lg font-medium">{hours}</p>
            )}
          </div>
          <div className="flex flex-col gap-2 mt-2">
            <p className="text-2xl font-medium">{title}</p>
            {text && (
              <p className="text-lg font-medium">{text}</p>
            )}
          </div>
        </div>
        <div className="flex justify-end">
          <p className="text-lg font-medium">{duration}</p>
        </div>
      </div>
    </div>
  );
};
